import { useEffect, useState } from "react";
import BlogList from "./BlogList";
 
const Home =() =>{
    
    //click event lec7
    // const handelclick=()=>{
    //     console.log('clickeddddddd');
    // }

    // const handelclickagain=(name)=>{
    //     console.log('hello', name);
    // }

    //set state hook lec 8
    // const [name,setName] = useState('mario');

    // const clickHandelthree=()=>{
    //     setName('wow');
    // }

    const [blogs,setBlog]= useState(null);
    const [isPending,setIsPending ]= useState(true);
    const [error,setError] = useState(null);

    //   use effect 

    useEffect(()=>{
      setTimeout(() => {
        fetch('http://localhost:8000/blogs')
        // this is async
          .then(res=>{
            console.log(res);
            if(!res.ok){
              console.log('cant fetch the resource---' );
            }
            return res.json();// this is also async
          })
          .then(data=>{
            console.log(data);
            setBlog(data);
            setIsPending(false);
          }
          )
          .catch((err)=>{
            // console.log(err.message);
            setError(err.message);
          })
      }, 1000);
      // remove this 1000 ms, its for devlopment understanding.
        // []--> this  controls depedency how ,what inffluences the useEffect behaviour. 
    } , []);

    return (
        <div className="home">


        {/*ON*/}
        {error && <div>{error}</div>}
        {isPending && <div>Loading.....</div>}
        {/* props usage */}
        {blogs && <BlogList  blogs={blogs}  title="All Title!" /> }
        
        {/* <BlogList  blogs={blogs.filter((blog)=>blog.author=== 'mario')}  title="Mario's Blog"/> */}



        {/*<p>{name}</p>*/}
        {/*<button onClick={clickHandelthree}>change name</button>*/}
        {/* click event */}
        {/*<button onClick={handelclick}>Click me</button>*/}
        {/* anonymous function */}
        {/*<button onClick={()=>handelclickagain('yooo')}>click me again</button>*/}



        </div>
    );
}

export default Home;
